  {"id": 178, "question": "لماذا قلّت الثورات العلوية في الغيبة الصغرى؟", "answer": "لكثرة الحركات المضادة المختلفة في العقيدة، ولانتهاء زمن حضور الائمة"},
  {"id": 179, "question": "من استقبل وفد القمّيّين بعد وفاة الإمام؟", "answer": "أُخبِروا بوفاته"},
  {"id": 180, "question": "من ذهب إليه الوفد بعد سماعهم عن الوصي؟", "answer": "جعفر"},
  {"id": 181, "question": "أين كان جعفر عندما جاء الوفد؟", "answer": "في دجلة مع المغنّين"},
  {"id": 182, "question": "ماذا طلب القميون من جعفر؟", "answer": "أن يخبرهم عن كمية الأموال"},
  {"id": 183, "question": "ماذا قال جعفر عن علم الغيب؟", "answer": "هذا علم الغيب"},
  {"id": 184, "question": "إلى من ذهب جعفر بعد فشله؟", "answer": "إلى الخليفة"},
  {"id": 185, "question": "من طلع على الوفد بعد خروجهم؟", "answer": "شاب"},
  {"id": 186, "question": "ماذا فعل الشاب مع الوفد؟", "answer": "صاح بأسمائهم وقال: أجيبوا مولاكم"},
  {"id": 187, "question": "ماذا فعل الإمام مع أبي العباس القمي؟", "answer": "دفع إليه شيئاً من الحنوط والكفن"},
  {"id": 188, "question": "ماذا قال الإمام لأبي العباس؟", "answer": "عظم الله أجرك في نفسك"},
  {"id": 189, "question": "أين توفي أبو العباس؟", "answer": "عند عقبة همدان"},
  {"id": 190, "question": "ماذا قال الإمام الهادي عن جعفر؟", "answer": "أمر أصحابه بالابتعاد عنه، لفسقه"},
  {"id": 191, "question": "ماذا قال ابن خاقان عن جعفر؟", "answer": "ومَن جعفر حتى يُسأل عنه أو يُقرَن بالحسن"},
  {"id": 192, "question": "ماذا فعل الإمام العسكري عندما أُطلق من السجن؟", "answer": "قال للرسول أنه وجعفر قد خرجا من الدار جميعاً"},
  {"id": 193, "question": "ما موقف جعفر الأول من الإمام المهدي؟", "answer": "ادعاؤه الإمامة"},
  {"id": 194, "question": "ما موقف جعفر الثاني من وريث أخيه؟", "answer": "ادعاؤه عدم وجود وريث، واستيلاؤه على الترِكة"},
  {"id": 195, "question": "ما موقف جعفر الثالث من المهدي؟", "answer": "وشايته بالإمام المهدي"},
  {"id": 196, "question": "ما العوامل التي ساعدت جعفر على ادعاء الإمامة؟", "answer": "عدم وجود وريث ظاهر، كتمان ولادة المهدي، استقبال المعزين، دعم السلطة"},
  {"id": 197, "question": "ما أسباب فشل جعفر؟", "answer": "سلوكه غير المرضي، تحذير الإمام منه، تخطيط الإمام العسكري، تواتر الروايات"},
  {"id": 198, "question": "كيف تصدى المهدي لجعفر في الصلاة؟", "answer": "منعه من الصلاة على أبيه وأبعاده"},
  {"id": 199, "question": "ماذا تضمن البيان الذي أصدره الإمام ضد جعفر؟", "answer": "الإشارة لأخطائه الإملائية، تأكيد اصطفاء الله للأئمة، نفي علمه، التذكير بعصيانه، تحديه بتفسير آية"},
  {"id": 200, "question": "ماذا قال التوقيع عن سبيل جعفر؟", "answer": "أما سبيل عمّي جعفر، فسبيل إخوة يوسف"},
  {"id": 201, "question": "ماذا قال الإمام السجاد عن الأنبياء؟", "answer": "في القائم منا سنن من الأنبياء"},
  {"id": 202, "question": "ما السنن من الأنبياء للقائم حسب السجاد؟", "answer": "موسى: الخوف والغيبة، عيسى: اختلاف الناس، أيوب: الفرج بعد البلوى، محمد: الخروج بالسيف"},
  {"id": 203, "question": "كم غيبة للقائم حسب السجاد؟", "answer": "غيبتين إحداهما أطول من الأخرى"},
  {"id": 204, "question": "ماذا قال الصادق عن غيبة القائم؟", "answer": "لقائم آل محمد غيبتان"},
  {"id": 205, "question": "ما العلامات التي تسبق ظهور القائم حسب الصادق؟", "answer": "اختلاف سيف بني فلان، ضيق الحلقة، ظهور السفياني، اشتداد البلاء"},
  {"id": 206, "question": "ما العلتان الأساسيتان للغيبة؟", "answer": "أن لا يكون في عنقه بيعة لطاغية، ومخافة القتل من الظالمين"},
  {"id": 207, "question": "لماذا حارب الظالمون أهل البيت حسب الرضا؟", "answer": "خوفهم على الخلافة، ومحاولة استئصال نسل النبي لمنع ولادة القائم"},
  {"id": 208, "question": "ما العلة الإضافية الأولى للغيبة الصغرى؟", "answer": "التمهيد للغيبة الكبرى"},
  {"id": 209, "question": "ما العلة الإضافية الثانية للغيبة الصغرى؟", "answer": "تكامل استقلال الكيان الشيعي فكرياً"},
  {"id": 210, "question": "ما التوقيع الذي حدد المرجعية في الحوادث؟", "answer": "وأما الحوادث الواقعة فارجعوا فيها إلى رواة حديثنا، فإنهم حجتي عليكم وأنا حجة الله"},
  {"id": 211, "question": "من السفير الأول؟", "answer": "الشيخ الموثوق أبو محمد عثمان بن سعيد العَمري"},
  {"id": 212, "question": "لماذا سمي العَمري؟", "answer": "نسبة إلى جده"},
  {"id": 213, "question": "لماذا يقال له العسكري؟", "answer": "لأنه كان يسكن العسكر، وهي سامراء"},
  {"id": 214, "question": "لماذا يقال له السمّان؟", "answer": "لأنه كان يتّجر بالسمن تغطية على الأمر"},
  {"id": 215, "question": "كيف كان عثمان يخفي الأموال؟", "answer": "يجعلها في جراب السمن وزقاقه، ويحملها تقية"},
  {"id": 216, "question": "من أولاد عثمان بن سعيد؟", "answer": "محمد وهو السفير الثاني، وأحمد"},
  {"id": 217, "question": "ماذا قال الإمام الهادي عن عثمان بن سعيد؟", "answer": "هذا أبو عمرو الثقة الأمين، ما قاله لكم فعني قوله، وما أداه إليكم فعنّي يؤديه"},
  {"id": 218, "question": "ماذا قال الإمام العسكري عن عثمان بن سعيد؟", "answer": "هذا أبو عمرو الثقة الأمين، ثقة الماضي، وثقتي في المحيا والممات"},
  {"id": 219, "question": "ماذا قال الإمام العسكري عن وكالة عثمان؟", "answer": "امض يا عثمان، فإنك الوكيل والثقة، المأمون على مال الله"},
  {"id": 220, "question": "من حضر تغسيل الإمام العسكري؟", "answer": "عثمان بن سعيد"},
  {"id": 221, "question": "أين دُفن عثمان بن سعيد؟", "answer": "في الجانب الغربي من بغداد في شارع الميدان"},
  {"id": 222, "question": "من أقام صندوقاً على قبر عثمان؟", "answer": "الرئيس أبو منصور محمد بن الفرج"},
  {"id": 223, "question": "ماذا كتب الإمام المهدي لمحمد بن عثمان؟", "answer": "إنا لله وإنا إليه راجعون، عاش أبوك سعيداً ومات حميداً، فرحمه الله وألحقه بأوليائه"},
  {"id": 224, "question": "من السفير الثاني؟", "answer": "أبو جعفر محمد بن عثمان بن سعيد العَمري"},
  {"id": 225, "question": "من نصّ على سفارة محمد بن عثمان؟", "answer": "الإمام العسكري، وأبوه بأمر من الإمام المهدي"},
  {"id": 226, "question": "ماذا قال الإمام العسكري عن العمري وابنه؟", "answer": "العمري وابنه ثقتان فما أديا فعني يؤديان"},
  {"id": 227, "question": "ماذا قال الإمام المهدي عن محمد بن عثمان؟", "answer": "لم يزل ثقتنا في حياة الأب رضي الله عنه وأرضاه، يجري عندنا مجراه ويسد مسده"},
  {"id": 228, "question": "كم مدة سفارة محمد بن عثمان؟", "answer": "نحواً من خمسين سنة"},
  {"id": 229, "question": "متى توفي محمد بن عثمان؟", "answer": "في جمادى الأولى سنة 305 هـ"},
  {"id": 230, "question": "هل كان لمحمد بن عثمان كتب؟", "answer": "نعم، كانت له كتب مصنفة في الفقه، منها: كتاب الأشربة"},
  {"id": 231, "question": "ماذا فعل محمد بن عثمان قبل موته؟", "answer": "حفر قبره وسواه بالساج"},
  {"id": 232, "question": "إلى من أوصى محمد بن عثمان؟", "answer": "إلى الحسين بن روح"},
  {"id": 233, "question": "أين دفن محمد بن عثمان؟", "answer": "عند والده في شارع باب الكوفة"},
  {"id": 234, "question": "من السفير الثالث؟", "answer": "أبو القاسم الحسين بن روح بن أبي بحر النوبختي"},
  {"id": 235, "question": "ما علاقة الحسين بن روح بمحمد بن عثمان؟", "answer": "كان وكيلاً مفضَّلاً له، ينظر في أملاكه"},
  {"id": 236, "question": "متى بدأ تحويل الأمور للحسين بن روح؟", "answer": "قبل موت محمد بن عثمان بسنتين أو ثلاث"},
  {"id": 237, "question": "ماذا قال محمد بن عثمان للجماعة عن الحسين؟", "answer": "هذا أبو القاسم الحسين بن روح، القائم مقامي، والسفير بينكم وبين صاحب الأمر"},
  {"id": 238, "question": "متى تولى الحسين بن روح السفارة فعلاً؟", "answer": "بموت أبي جعفر العمري سنة 305 هـ"},
  {"id": 239, "question": "ما مسلك الحسين بن روح في السفارة؟", "answer": "الالتزام بالتقية المضاعفة، بإظهار الاعتقاد بمذهب أهل السنة"},
  {"id": 240, "question": "متى توفي الحسين بن روح؟", "answer": "سنة 326هـ"},
  {"id": 241, "question": "من السفير الرابع؟", "answer": "أبو الحسن علي بن محمد السَّمَرى"},
  {"id": 242, "question": "متى تولى السمري السفارة؟", "answer": "من حين وفاة أبي القاسم بن روح سنة 326هـ"},
  {"id": 243, "question": "كم مدة سفارة السمري؟", "answer": "ثلاثة أعوام كاملة غير أيام"},
  {"id": 244, "question": "متى توفي السمري؟", "answer": "سنة 329هـ"},
  {"id": 245, "question": "ما التوقيع الذي خرج للسمري قبل وفاته؟", "answer": "بسم الله الرحمن الرحيم، يا علي بن محمد السمرى! أعظم الله أجر إخوانك فيك، فإنك ميت ما بينك وبين ستة أيام"},
  {"id": 246, "question": "ماذا أمر الإمام السمري في التوقيع؟", "answer": "فاجمع أمرك ولا توص إلى أحد فيقوم مقامك بعد وفاتك، فقد وقعت الغيبة التامة"},
  {"id": 247, "question": "من سُئل السمري عن وصيه؟", "answer": "قيل له: من وصيك من بعدك؟"},
  {"id": 248, "question": "ماذا أجاب السمري عن الوصية؟", "answer": "لله أمر هو بالغه"},
  {"id": 249, "question": "أين دفن السمري؟", "answer": "في الشارع المعروف بشارع الخلنجي من ربع المحول"},
  {"id": 250, "question": "ما الخصائص العامة للسفراء؟", "answer": "تشابه نشاط وتحرك السفراء، انحصار السفارة بغير العلويين، بغداد مسرحاً لتحركهم"},
  {"id": 251, "question": "لماذا صُرفت السفارة عن العلويين؟", "answer": "لأن الصورة الأولى التي تحملها الدولة عن كل علوي، هو كونه موالياً للأئمة، وثائراً على الظلم"},
  {"id": 252, "question": "ما معنى التوقيع؟", "answer": "الكلمات القصار التي تمليها أقلام الكبراء في ذيل الرسائل والعرائض"},
  {"id": 253, "question": "هل التوقيعات مقتصرة على الأسئلة؟", "answer": "لا، بل كانت تتخذ أحياناً شكل بيان ابتدائي"},
  {"id": 254, "question": "كم يحتاج خروج التوقيع من الوقت؟", "answer": "حوالي اليومين أو الثلاثة"},
  {"id": 255, "question": "ماذا فعل السفير الثالث في المجلس العام؟", "answer": "فضل الخلفاء الثلاثة على أمير المؤمنين"},
  {"id": 256, "question": "ما نتيجة موقف السفير الثالث؟", "answer": "رفعه العامة وكثر الدعاء له"},
  {"id": 257, "question": "كم وكيلاً أحصى الشيخ الصدوق؟", "answer": "اثني عشر وكيلاً"},
  {"id": 258, "question": "كم وكيلاً أضاف الصدر للعدد؟", "answer": "ستة"},
  {"id": 259, "question": "كيف كان تعيين الوكلاء؟", "answer": "مباشرة من الإمام"},
  {"id": 260, "question": "ما المهمتان الأساسيتان للسفارة؟", "answer": "تهيئة الأذهان للغيبة الكبرى، والقيام بمصالح المجتمع"},
  {"id": 261, "question": "متى بدأ التزوير في السفارة؟", "answer": "في عهد السفير الثاني"},
  {"id": 262, "question": "لماذا لم يكن تزوير في عهد السفير الأول؟", "answer": "لتأريخه المجيد، والغيبة في بدايتها، وتتبع السلطات قوي، والتزوير لا يحتمل وجوده قبل الاعتياد"},
  {"id": 263, "question": "من أول من ادعى السفارة زوراً؟", "answer": "الشريعي"},
  {"id": 264, "question": "من المدعون الآخرون للسفارة زوراً؟", "answer": "النميري، الكرخي، البلالي، البغدادي، إسحاق الأحمر، الباقطاني"},
  {"id": 265, "question": "من أشد المدعين تأثيراً على ابن روح؟", "answer": "الشلمغاني"},
  {"id": 266, "question": "ما حال الشلمغاني في البداية؟", "answer": "كان وكيلاً لابن روح"},
  {"id": 267, "question": "من آخر مدعي السفارة؟", "answer": "أبو دلف الكاتب"},
  {"id": 268, "question": "ما موقف الراوي من أبي دلف؟", "answer": "فلعنّاه وبرئنا منه لأن عندنا أنّ كلّ من ادعى الأمر بعد السَّمَري فهو كافر ضال"},
  {"id": 269, "question": "ماذا قال الفتى عن المفوضة؟", "answer": "جئت تسألُ عن مقالة المفوّضة، كذبوا، بل قلوبنا أوعية لمشيئة الله"},
  {"id": 270, "question": "لماذا كان الاهتمام بمحاربة المدعين كبيراً؟", "answer": "لأن العمل ينسجم مع سياسة السلطات، ولأن الهدم الداخلي أضرّ من الخارجي"},
  {"id": 271, "question": "لماذا لم تستفد السلطة من المدعين؟", "answer": "لأن دعوتهم كانت مكشوفة الانحراف، ولموقف السفراء القوي، وخوف الدولة على قواعدها"},
  {"id": 272, "question": "ما أول نقطة في أعمال السفراء؟", "answer": "إقامة الحجة على صدقهم"},
  {"id": 273, "question": "كيف أقام السفراء الحجة على صدقهم؟", "answer": "بكونهم صادقين ورعين، وإفحام مدعي السفارة، وإظهار كذبهم"},
  {"id": 274, "question": "ماذا قال ابن روح للراوي الشاك؟", "answer": "لئن أَخِرُّ من السماء فتخطفني الطير، أحبُّ إليَّ من أن أقول في دين الله برأيي"},
  {"id": 275, "question": "بماذا تكلم ابن روح مع المرأة من آبة؟", "answer": "بلغة قومها"},
  {"id": 276, "question": "بماذا أخبر السَّمَري قبل وفاة ابن بابويه؟", "answer": "بوفاة علي بن الحسين بن بابويه"},
  {"id": 277, "question": "ماذا أخرج العَمري لابن متيل؟", "answer": "ثويبات وصرراً"},
  {"id": 278, "question": "لماذا كانت الثياب والصرة؟", "answer": "الثياب كفن لمحمد بن عبيد الله الحايري، والصرة كراء الحمّالين والحُفّار"},
  {"id": 279, "question": "ماذا طلب الشلمغاني من ابن روح؟", "answer": "المباهلة"},
  {"id": 280, "question": "ما جواب ابن روح على المباهلة؟", "answer": "أينا تقدم صاحبَه فهو المخصوم"},
  {"id": 281, "question": "متى قُتل الشلمغاني؟", "answer": "سنة 323هـ"},
  {"id": 282, "question": "ماذا قيل عن ابن روح في كتمان السر؟", "answer": "لو كان الحجة تحت ذيله وقُرّض بالمقاريض لَما كشفه"},
  {"id": 283, "question": "بماذا نُهي الحميري وأحمد بن إسحاق؟", "answer": "نُهيتم عن السؤال عن الاسم"},
  {"id": 284, "question": "ما التوقيع عن ذكر الاسم؟", "answer": "إما السكوت والجنة، وإما الكلام والنار"},
  {"id": 285, "question": "لماذا التحريم عن ذكر الاسم؟", "answer": "إنهم إن وقفوا على الاسم أذاعوه، وإن وقفوا على المكان دلّوا عليه"},
  {"id": 286, "question": "ماذا قال الإمام الهادي عن ذكر الاسم؟", "answer": "لا يحل ذكره باسمه"},
  {"id": 287, "question": "ماذا قال الصادق عن من يسمي المهدي؟", "answer": "صاحب هذا الأمر لا يسميه باسمه إلا كافر"},
  {"id": 288, "question": "لماذا اصطحب العَمري أبا طاهر بن بلال؟", "answer": "ليُريه الإمام ويأمره بحمل المال"},
  {"id": 289, "question": "بماذا اعترف ابن بلال بعد رؤية الإمام؟", "answer": "اعترف بعدم معرفته بصاحب الزمان"},
  {"id": 290, "question": "كيف كان العَمري يتسلم الأموال؟", "answer": "بصفته تاجراً، ولا يدفع بها وصلاً"},
  {"id": 291, "question": "ماذا فعل ابن روح مع من لعن معاوية؟", "answer": "أمر بطرد البوّاب"},
  {"id": 292, "question": "ما الأمر الأول في التوقيعات؟", "answer": "حل المشاكل العائلية"},
  {"id": 293, "question": "ما الأمر الثاني في التوقيعات؟", "answer": "تيسير الشفاء"},
  {"id": 294, "question": "ما الأمر الثالث في التوقيعات؟", "answer": "طلب الولد"},
  {"id": 295, "question": "لمن دعا الإمام بالولد عبر الأسدي؟", "answer": "لابن بابويه فقط"},
  {"id": 296, "question": "ما الأمر الرابع في التوقيعات؟", "answer": "سؤال الدعاء لمهام الأمور"},
  {"id": 297, "question": "ما الأمر الخامس في التوقيعات؟", "answer": "الاستئذان بالسفر"},
  {"id": 298, "question": "ما الأمر السادس في التوقيعات؟", "answer": "الاستئذان بالحج"},
  {"id": 299, "question": "ما الأمر السابع في التوقيعات؟", "answer": "طلب الأكفان"},
  {"id": 300, "question": "متى احتاج الصَيمري للكفن حسب التوقيع؟", "answer": "سنة إحدى وثمانين"},
  {"id": 301, "question": "ما الأمر الثامن في التوقيعات؟", "answer": "تحذير الوكلاء"},
  {"id": 302, "question": "من علم بوجود الوكلاء؟", "answer": "الوزير عبد الله بن سليمان"},
  {"id": 303, "question": "كيف كانت صفة الإمام عند وفاة أبيه؟", "answer": "صبياً، بوجهه سمرة، بشعره قَطَط، وبأسنانه تفليج"},
  {"id": 304, "question": "كيف وُصف الإمام بعد ذلك؟", "answer": "شابٌ حسن الوجه، طيب الرائحة، هيوب"},
  {"id": 305, "question": "كيف وُصف الإمام في رواية أخرى؟", "answer": "شاب أسمر، معتدل القامة"},
  {"id": 306, "question": "كيف وُصف خدّ الإمام؟", "answer": "على خدّه الأيمن خال"},
  {"id": 307, "question": "ماذا وصف محمد بن عثمان من الإمام؟", "answer": "عنقه في غلظه، ليدل على صفة الرجولة"},
  {"id": 308, "question": "كيف كان لباس الإمام في الإحرام؟", "answer": "متزراً ببردة ومتشحاً بأخرى"},
  {"id": 309, "question": "كم بقي الإمام في سامراء بعد وفاة أبيه؟", "answer": "تسعة عشر سنة"},
  {"id": 310, "question": "لماذا بَعد الوكلاء عن الإمام؟", "answer": "لكي لا يوجّهوا الأنظار، وليعيشوا في قلب الحوادث، وليبعدوا عن مراقبة الدولة"},
  {"id": 311, "question": "أين كان الإمام يسكن خلال الحج؟", "answer": "في تلك الديار المقدسة"},
  {"id": 312, "question": "أين ذهب الإمام يوم عرفة؟", "answer": "إلى كربلاء"},
  {"id": 313, "question": "أين نزل الإمام في الإسكندرية؟", "answer": "في خان للغرباء"},
  {"id": 314, "question": "ماذا قال الإمام لابن مهزيار عن سكنه؟", "answer": "أبي عهِد إليَّ أن لا أجاور قوماً غضب الله عليهم، وأمرني أن لا أسكن من الجبال إلا وعرها"},
  {"id": 315, "question": "كم عمر الإمام عند وفاة أبيه؟", "answer": "أربع سنوات وستة أشهر"},
  {"id": 316, "question": "كم عمر الإمام عند وفاة السفير الثاني؟", "answer": "خمسين عاماً"},
  {"id": 317, "question": "كم عمر الإمام عند وفاة السفير الثالث؟", "answer": "واحداً وسبعين سنة"},
  {"id": 318, "question": "كم عمر الإمام عند وفاة السَّمَري؟", "answer": "أربعاً وسبعين سنة"},
  {"id": 319, "question": "كم قضى الإمام في حياة أبيه؟", "answer": "أربع سنين ونصفاً"},
  {"id": 320, "question": "كم قضى الإمام في الغيبة الصغرى؟", "answer": "تسعة وستين عاماً ونصفاً"},
  {"id": 321, "question": "كم كان عمر الإمام سنة 332 هـ؟", "answer": "ستاً وسبعين سنة وأحد عشر شهراً"},
  {"id": 322, "question": "كم كان يقدر الناظر عمر الإمام؟", "answer": "بحوالي العشرين"},
  {"id": 323, "question": "ما الاعتباران لبقاء الإمام شاباً؟", "answer": "الإيمان بقدرة الله المطلقة، ونموّه البطيء في غيبته"},
  {"id": 324, "question": "ماذا أشار الإمام الحسين عن عودة المهدي؟", "answer": "أن الناس قد ينكرونه لعودته شاباً موفقاً"},
  {"id": 325, "question": "ما معنى الموفق؟", "answer": "معتدل الخلقة وتناسق الأعضاء، أو من بلغ أشدّه وتوسط في الشباب"},
  {"id": 326, "question": "ما النشاط الأول للإمام في الغيبة؟", "answer": "وجوده وإمامته"},
  {"id": 327, "question": "ما النشاط الثاني للإمام؟", "answer": "صيانة تراث آبائه"},
  {"id": 328, "question": "ما الذي دُوّن في عصر الغيبة الصغرى؟", "answer": "الأصول الأربعمائة"},
  {"id": 329, "question": "من مهّد للوكالة قبل الإمام المهدي؟", "answer": "الإمام الهادي بتوثيق عثمان بن سعيد"},
  {"id": 330, "question": "كم شخصاً رأى الإمام حسب الشيخ الصدوق؟", "answer": "ثمانية وستون شخصاً"},
  {"id": 331, "question": "كم شخصاً رأى الإمام حسب الميرزا النوري؟", "answer": "ثلاثمائة وأربعة أشخاص"},
  {"id": 332, "question": "متى التقى عيسى الجوهري بالإمام؟", "answer": "قرب المدينة المنورة سنة ٢٦٨هـ"},
  {"id": 333, "question": "لماذا رأى عيسى الجوهري الإمام؟", "answer": "لتكذيب المنكرين لوجوده"},
  {"id": 334, "question": "ما كان أحد أهداف القبض على المهدي؟", "answer": "أحد الأهداف الكبرى للدولة"},
  {"id": 335, "question": "كم حملة قامت بها السلطة؟", "answer": "ثلاث حملات"},
  {"id": 336, "question": "من قام بحملة واحدة؟", "answer": "المعتمد"},
  {"id": 337, "question": "من قام بحملتين؟", "answer": "المعتضد"},
  {"id": 338, "question": "ما الذي انشغلت به الدولة في عهد المعتمد؟", "answer": "حرب الزنج"},
  {"id": 339, "question": "من قاد الحملة الأولى للمعتضد؟", "answer": "ثلاثة أشخاص منهم رشيق"},
  {"id": 340, "question": "ماذا وجدوا في الدار؟", "answer": "غرفة فيها بحر ماء، وفي أقصاه حصير على الماء، وفوقه رجل يصلي"},
  {"id": 341, "question": "ماذا حدث للرجلين الأولين؟", "answer": "غرقا"},
  {"id": 342, "question": "ماذا حلف المعتضد بعد عودة رشيق؟", "answer": "أنا نفيٌ من جدّي، وحلف بأشدّ أيمان له"},
  {"id": 343, "question": "ماذا كان يقرأ الإمام في الحملة الثانية؟", "answer": "القرآن"},
  {"id": 344, "question": "ماذا قال الأمير للجند؟", "answer": "انزلوا عليه"},
  {"id": 345, "question": "ما جواب الجند؟", "answer": "أليس هو الذي مرّ عليك؟"},
  {"id": 346, "question": "ماذا قال الإمام في التوحيد؟", "answer": "إن الله تعالى هو الذي خلق الأجسام وقسّم الأرزاق، لأنه ليس بجسمٍ"},
  {"id": 347, "question": "ما مقام الأئمة حسب الإمام؟", "answer": "قدوةُ الله وأئمةٌ، وخلفاءُ الله في أرضه، وأمناؤُهُ على خلقه"},
  {"id": 348, "question": "لماذا خلق الله الخلق؟", "answer": "لم يخلقهم عبثاً بل خلقهم بقدرته"},
  {"id": 349, "question": "لماذا بعث الله النبيين؟", "answer": "مبشرين ومنذرين"},
  {"id": 350, "question": "ما الفرقان بين الحجة وإخوانهم؟", "answer": "عَصَمهم من الذنوب، وبرَّأهُمْ من العيوب"},
  {"id": 351, "question": "ماذا قال الإمام عن علم جعفر؟", "answer": "فوالله ما يعرف حلالاً من حرام"},
  {"id": 352, "question": "ماذا قال الإمام عن ورع جعفر؟", "answer": "فالله شهيد على تركه الصلاة"},
  {"id": 353, "question": "ما الذي رفضه الإمام في الإمامة؟", "answer": "أن تكون الإمامة في أخوين بعد الحسن والحسين"},
  {"id": 354, "question": "ماذا قال الإمام عن خلو الأرض؟", "answer": "أنَّ الأرض لا تخلو من حُجَّةٍ إما ظاهراً وإما مغموراً"},
  {"id": 355, "question": "ماذا قال الفتى عن المفوّضة؟", "answer": "كذبوا، بل قلوبنا أوعية لمشيئة الله"},
  {"id": 356, "question": "من يعلم الغيب؟", "answer": "لا يعلم الغيب غيرُه"},
  {"id": 357, "question": "ماذا قال الإمام عن الشلمغاني؟", "answer": "قد ارتد عن الإسلام وادّعى ما كفر معه بالخالق"},
  {"id": 358, "question": "ماذا قال الإمام عن قبول الأموال؟", "answer": "لا نقبلها إلا لتطهروا"},
  {"id": 359, "question": "ماذا قال الإمام عن ظهور الفرج؟", "answer": "فإنه إلى الله، وكذب الوقاتون"},
  {"id": 360, "question": "ماذا قال الإمام عن الخمس؟", "answer": "قد أبيح لشيعتنا"},
  {"id": 361, "question": "لماذا لم تكن بيعة في عنق الإمام؟", "answer": "لم يكن أحد من آبائي إلّا وقد وقعت في عنقه بيعة لطاغية زمانه"},
  {"id": 362, "question": "ما وجه الانتفاع بالإمام في غيبته؟", "answer": "كالانتفاع بالشمس إذا غيّبها السحاب"},
  {"id": 363, "question": "بمن تأسى الإمام؟", "answer": "في ابنة رسول الله - صلى الله عليه وعليها - لي أسوة حسنه"}
